mapper.select:
select blog.*, user.nickname as user_name, case when cnt is null then 0 else cnt end as praise_count from blog left join user on user.user_id = blog.user_id left join (select blog.blog_id, count(praise.user_id) cnt from blog left join praise on blog.blog_id = praise.blog_id group by praise.blog_id) tmp on blog.blog_id = tmp.blog_id
1 = 1
and blog.user_id = #{userId}
and title like concat('%',#{title},'%')
and content like concat('%',#{content},'%')
and type = #{type}
order by time desc
limit #{offset},#{size};

mapper.insert:
insert into blog (content, user_id,
  time, title, type
  )
values ( #{content,jdbcType=VARCHAR}, #{userId,jdbcType=VARCHAR},
  now(), #{title,jdbcType=VARCHAR}, #{type,jdbcType=VARCHAR}
  )
